import React, { useState, useEffect } from 'react';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';

function App() {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks from backend
  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await fetch("/tasks");
      const data = await response.json();
      setTasks(data);  // Update the state with the latest tasks from the server
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };
  
  const addTask = async (task) => {
    const res = await fetch('http://localhost:5000/tasks', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(task),
    });
    const newTask = await res.json();
    setTasks([...tasks, newTask]);
  };

  const updateTask = async (id, updatedTask) => {
    try {
      const response = await fetch(`/tasks/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedTask),
      });
      
      if (response.ok) {
        // After successful update, fetch updated tasks list
        fetchTasks();  // Re-fetch tasks to display the updated list
      } else {
        alert("Failed to update task");
      }
    } catch (error) {
      console.error("Error updating task:", error);
      alert("Error updating task");
    }
  };
  

  const deleteTask = async (id) => {
    try {
      const response = await fetch(`/tasks/${id}`, {
        method: "DELETE",
      });
      
      if (response.ok) {
        // Immediately remove the deleted task from the state
        setTasks(tasks.filter(task => task._id !== id));
      } else {
        alert("Failed to delete task");
      }
    } catch (error) {
      console.error("Error deleting task:", error);
      alert("Error deleting task");
    }
  };
  

  return (
    <div className="App">
      <h1>Task Manager</h1>
      <TaskForm addTask={addTask} />
      <TaskList tasks={tasks} updateTask={updateTask} deleteTask={deleteTask} />
    </div>
  );
}

export default App;
