import React, { useState, useEffect } from "react";

const TaskForm = ({ addTask, updateTask, taskToEdit }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [isCompleted, setIsCompleted] = useState(false);

  // Effect to load task data for editing
  useEffect(() => {
    if (taskToEdit) {
      setTitle(taskToEdit.title);
      setDescription(taskToEdit.description);
      setIsCompleted(taskToEdit.isCompleted);
    }
  }, [taskToEdit]);

  // Handle form submission to either add or update a task
  const handleSubmit = (e) => {
    e.preventDefault();

    const task = {
      title,
      description,
      isCompleted,
    };

    // If editing a task, update it. Otherwise, add a new task.
    if (taskToEdit) {
      updateTask(taskToEdit._id, task); // Pass _id along with task data
    } else {
      addTask(task);
    }

    // Clear form inputs after submission
    setTitle("");
    setDescription("");
    setIsCompleted(false);
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Title Input */}
      <div>
        <input
          type="text"
          placeholder="Task Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>

      {/* Description Input */}
      <div>
        <textarea
          placeholder="Task Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
      </div>

      {/* Completed Status Checkbox */}
      <div>
        <label>
          <input
            type="checkbox"
            checked={isCompleted}
            onChange={(e) => setIsCompleted(e.target.checked)}
          />
          Mark as completed
        </label>
      </div>

      {/* Submit Button */}
      <div>
        <button type="submit">{taskToEdit ? "Update" : "Add"} Task</button>
      </div>
    </form>
  );
};

export default TaskForm;
