import React, { useState, useEffect } from "react";
import axios from "axios";
import TaskItem from "./components/TaskItem";
import TaskForm from "./components/TaskForm";
import Pagination from "./components/Pagination";
import "./styles.css"; // Ensure you have added your styles here or link to Bootstrap in your `index.html`

function App() {
  const [tasks, setTasks] = useState([]);
  const [currentTask, setCurrentTask] = useState(null);
  const [filter, setFilter] = useState("all");
  const [currentPage, setCurrentPage] = useState(1);
  const [tasksPerPage] = useState(5);

  // Fetch tasks from the backend
  useEffect(() => {
    const fetchTasks = async () => {
      const response = await axios.get("/api/tasks");
      setTasks(response.data);
    };

    fetchTasks();
  }, []);

  // Add a task
  const addTask = async (newTask) => {
    const response = await axios.post("/api/tasks", newTask);
    setTasks([...tasks, response.data]);
  };

  // Update a task
  const updateTask = async (updatedTask) => {
    const response = await axios.put(`/api/tasks/${updatedTask._id}`, updatedTask);
    setTasks(tasks.map((task) => (task._id === updatedTask._id ? response.data : task)));
    setCurrentTask(null); // Reset form after update
  };

  // Delete a task
  const deleteTask = async (id) => {
    await axios.delete(`/api/tasks/${id}`);
    setTasks(tasks.filter((task) => task._id !== id));
  };

  // Edit a task
  const editTask = (task) => {
    setCurrentTask(task);
  };

  // Filter tasks (all, completed, incomplete)
  const filteredTasks = tasks.filter((task) => {
    if (filter === "completed") return task.completed;
    if (filter === "incomplete") return !task.completed;
    return true;
  });

  // Get current tasks for the current page
  const indexOfLastTask = currentPage * tasksPerPage;
  const indexOfFirstTask = indexOfLastTask - tasksPerPage;
  const currentTasks = filteredTasks.slice(indexOfFirstTask, indexOfLastTask);

  // Handle pagination change
  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="container my-5">
      <h1 className="text-center">Task Manager</h1>

      {/* Task Form */}
      <TaskForm
  addTask={addTask} // This makes sure addTask is passed to TaskForm
  updateTask={updateTask}
  taskToEdit={currentTask}
/>


      {/* Filters */}
      <div className="d-flex justify-content-between align-items-center mt-4">
        <h5>Tasks</h5>
        <div>
          <button
            className={`btn btn-sm ${filter === "all" ? "btn-primary" : "btn-outline-primary"} mx-1`}
            onClick={() => setFilter("all")}
          >
            All
          </button>
          <button
            className={`btn btn-sm ${filter === "completed" ? "btn-success" : "btn-outline-success"} mx-1`}
            onClick={() => setFilter("completed")}
          >
            Completed
          </button>
          <button
            className={`btn btn-sm ${filter === "incomplete" ? "btn-warning" : "btn-outline-warning"} mx-1`}
            onClick={() => setFilter("incomplete")}
          >
            Incomplete
          </button>
        </div>
      </div>

      {/* Task List */}
      <div className="list-group mt-3">
        {currentTasks.length > 0 ? (
          currentTasks.map((task) => (
            <TaskItem
              key={task._id}
              task={task}
              onDelete={deleteTask}
              onEdit={editTask}
            />
          ))
        ) : (
          <p className="text-center text-muted">No tasks available.</p>
        )}
      </div>

      {/* Pagination */}
      {filteredTasks.length > tasksPerPage && (
        <Pagination
          tasksPerPage={tasksPerPage}
          totalTasks={filteredTasks.length}
          currentPage={currentPage}
          onPageChange={handlePageChange}
        />
      )}
    </div>
  );
}

export default App;
