import axios from "axios";

const API_URL = "http://localhost:5000/tasks";

// Fetch all tasks
export const fetchTasks = async (page = 1, limit = 5) => {
    return await axios.get(`${API_URL}?page=${page}&limit=${limit}`);
  };
  

// Create a new task
const addTask = async (newTask) => {
  try {
    const response = await axios.post("http://localhost:5000/api/tasks", newTask); // Check if your backend is running on port 5000
    setTasks([...tasks, response.data]); // Update the tasks state
  } catch (error) {
    console.error("Error adding task:", error.response || error.message);  // Log the error details
  }
};

// Update a task
export const updateTask = async (id, updatedTask) => {
  return await axios.put(`${API_URL}/${id}`, updatedTask);
};

// Delete a task
export const deleteTask = async (id) => {
  return await axios.delete(`${API_URL}/${id}`);
};
